description.value:
Everything is in the title. Zend_Form->setIsArray does not work with decorators.

{code}
        $screen_name = $this->createElement('text','screen_name')
	            ->addFilter('stringTrim')
				->addFilter('stripTags')
				->setIsArray(true);
{code}

result is : 

<input type="text" name="screen_name[]" id="screen_name" value="" />

{code}
        $screen_name = $this->createElement('text','screen_name')
	            ->addFilter('stringTrim')
				->addFilter('stripTags')
				->setDecorators(array('Col'))
				->setIsArray(true);
{code}

result is : 

<input type="text" name="screen_name" id="screen_name" value="" helper="formText" multiple="" />


the [] disappears...

value.body:
{code}
<?php
class Lovonair_Form_Decorator_Col extends Zend_Form_Decorator_Abstract  implements Zend_Form_Decorator_Marker_File_Interface  
{
    public function buildLabel()
    {
        $element = $this->getElement();
        $label = $element->getLabel();
        if ($translator = $element->getTranslator()) {
            $label = $translator->translate($label);
        }
        if ($element->isRequired()) {
            $label .= '*';
        }
        $label .= '';
        $messages = $element->getMessages();   
        if (empty($messages)){
             return $element->getView()
                       ->formLabel($element->getName(), $label);
        }else{
             return $element->getView()
                       ->formLabel($element->getName(), $label, array('class'=>'error'));
        }
    }

    public function buildInput()
    {
        $element = $this->getElement();
        $helper  = $element->helper;
                   
            return $element->getView()->$helper(
                $element->getName(),
                $element->getValue(),
                $element->getAttribs(),
                $element->options
            );
        
    }

    public function buildErrors()
    {
        $element  = $this->getElement();
        $messages = $element->getMessages();        
        if (empty($messages)) {
            return '';
        }
        $return = '';     
           
        foreach($messages as $mess){
         $return .= '<p class="formError">' . $mess . '</p>';      
        }
        return $return;
    }

    public function buildDescription()
    {
        $element = $this->getElement();
        $desc    = $element->getDescription();
        if (empty($desc)) {
            return '';
        }
        return '<p class="formInfo">' . $desc . '</p>';
    }

    public function render($content)
    {         
        $element = $this->getElement();
        if (!$element instanceof Zend_Form_Element) {
            return $content;
        }
        if (null === $element->getView()) {
            return $content;
        }
        
    
        $separator = $this->getSeparator();
        $placement = $this->getPlacement();
        $label     = $this->buildLabel();
        $input     = $this->buildInput();
        $errors    = $this->buildErrors();
        $desc      = $this->buildDescription();
        if(!empty($errors)) $errorClass = " formRed"; else $errorClass = "";       
        if(!empty($this->_options) && isset($this->_options['class'])) $class = ' '.$this->_options['class']; else $class='';
        if(!empty($this->_options) && isset($this->_options['id'])) $id = ' id="'.$this->_options['id'].'"'; else $id='';
        if(!empty($this->_options) && isset($this->_options['style'])) $style = ' style="'.$this->_options['style'].'"'; else $style='';
        
        if($element instanceof Zend_Form_Element_MultiCheckbox){ 
            $inputr = '<div style="overflow:none; display:block; float:left;" class="checkbox">';
            $inputr .= $input;
            $inputr .= '</div>';
            $input = $inputr; 
        } 
        
        if($element instanceof Zend_Form_Element_Submit || $element instanceof Zend_Form_Element_Button){
        
          $output = '<div class="formAction" '.$id.'>'
                  . '<span class="btn'.$class.'">
                        <span class="btnBkg"></span>'                             
                  . $input   
                  . '</span>'  
                  . '</div>';   
               
        }elseif($element instanceof Zend_Form_Element_Checkbox && (isset($this->_options['type']) && $this->_options['type']=='term')){
         $output = '<div class="formCol'.$class.''.$errorClass.'" '.$id.' '.$style.'>'
                . '<p class="formSingle" id="'.$element->getName().'-element">'
                . '<label class="formCheck" for="'.$element->getName().'">'.$input.' '.$element->getLabel().'</label>'
                . '</p>'
                . $errors        
                . '<div class="formClear"></div>'           
                . '</div>';
        }else{   
        $output = '<div class="formCol'.$class.''.$errorClass.'" '.$id.' '.$style.'>'
                . '<p class="formLabel">'.$label.'</p>'                  
                . '<p class="formElement" id="'.$element->getName().'-element">'.$input.'</p>' 
                . $desc
                . $errors        
                . '<div class="formClear"></div>'     
                . '</div>';
        }

        switch($placement) {
            case (self::PREPEND):
                return $output . $separator . $content;
            case (self::APPEND):
            default:
                return $content . $separator . $output;
        }
    }
}
