 $SubFormMailTemplates = new Zend_Form_SubForm();
 $SubFormMailTemplates ->setLegend("mailtemplates");
 $areaOrig   = new Zend_Form_Element_Textarea("orig");
 $areaOrig->setLabel("origmail");
