     public function getFullyQualifiedName()
     {
-        return $this->getName();
+        $name = $this->getName();
+
+        if ($this->isArray() &&
+            null !== ($elementsBelongTo = $this->getElementsBelongTo())) {
+                $name .= '/' . $elementsBelongTo;
+        }
+
+        if (null !== ($belongsTo = $this->getBelongsTo())) {
+            $name = $belongsTo . '/' . $name;
+        }
+
+        $path = explode('/', strtr($name, array('[' => '/', ']' => '')));
+
+        $name = array_shift($path);
+        
+        if (!empty($path)) {
+           $name .=  '[' . join($path, '][') . ']';
+        }
+        return $name;
     }
 
